const images = [
    {
        src: "https://www.demarini.com/sites/demarini.com/files/Length_640x500_1.jpg?",
        isBat: true
    },
    {
        src: "data:image/jpeg;base64,",
        isBat: false
    },
    {
        src: "https://media.wired.com/photos/65404268bbee7fc2f21d8874/4:3/w_1800,h_1350,c_limit/vampirebat-science-MFGAJ9.jpg",
        isBat: true
    },
    {
        src: "data:image/jpeg;base64,",
        isBat: true
    },
    {
        src: "https://t3.ftcdn.net/jpg/02/75/74/58/360_F_275745846_slBI2EsTudIShef6hMliS6Oa123tC9Zv.jpg",
        isBat: false
    },
    {
        src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8yOOTBaWaeEDSXyFaTMxoeqC0LRRP-De4S9sJx7k49nVn2Ovk1086SA8-ZHaCGaM8pps&usqp=CAU",
        isBat: true
    },
    {
        src: "data:image/jpeg;base64,",
        isBat: false
    },
    {
        src: "https://s.abcnews.com/images/Technology/science-baseball-cricket-bats-01-gty-llr-210522_1621699849823_hpMain.jpg",
        isBat: true
    },
    {
        src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTo2DdJkchBk1HfmwiMjGSnjcCAEw2sxJihPQ&usqp=CAU",
        isBat: true
    },
]

export default function SelectPicture({ onCorrectAnswer, onIncorrectAnswer }) {
    const [selectedImages, setSelectedImages] = useState([])
    const handleImageClick = (index) => {
        setSelectedImages(c => {
            if (c.includes(index)) {
                return c.filter(imageIndex => imageIndex !== index)
            }

            return [...c, index];
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        const isAllCorrect = images.every(({ isBat }, index) => selectedImages.includes(index) === isBat)
        if (isAllCorrect) {
            onCorrectAnswer()
        } else {
            onIncorrectAnswer()
        }
    }

    return (
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: 16 }}>
            <p>Select all images of <strong>bats</strong></p>
            <Grid gutter="xs">
                {images.map(({ src }, index) => (
                    <Grid.Col span={4} key={src}>
                        <div onClick={() => handleImageClick(index)} style={{ cursor: "pointer", border: selectedImages.includes(index) ? "10px solid blue" : "" }}>
                            <Image h={150} src={src} />
                        </div>
                    </Grid.Col>
                ))}
            </Grid>
            <Button type="submit">Submit</Button>
        </form>
    )
}